check_permission() {
    if [ ${dryrun} == true ]; then
        :
    else
        if [ $(id -u) -ne 0 ]; then
            echo "必須以 root 權限執行此腳本！";
            exit 1;
        fi
    fi
}

create_prompt() {
    read -p "即將建立使用者帳戶，確認使用者資料是否無誤?（Yes/No）" yn
    echo 

    case $yn in
        [Yy]* ) create_users;;
        [Nn]* ) exit 0;;
    esac
}
